let map = new Map();

const setConfig = (name, value) => map.set(name, value);
// const getConfig = name => map.get(name);

const getConfig = name => {
    let baseConfigUrl = __dirname.split('node_modules')[0];
    baseConfigUrl = baseConfigUrl == ('/'||'') ?'./' : baseConfigUrl;
    const envUrl = baseConfigUrl + '.env.js';
    console.log('========.env.js =======URL:' + envUrl)
    const configs = require(envUrl).default || require(envUrl);
    return configs[name.toUpperCase()]
}

module.exports = {
    setConfig,
    getConfig
}