const crypto = require("crypto")
const formatDateTime = time => {
    if (!time) return '-'
    const t = new Date(time);
    const year = t.getFullYear();
    const month = t.getMonth() + 1;
    const date = t.getDate() + 1;
    const hour = getNum(t.getHours());
    const minute = getNum(t.getMinutes());
    return year + '-' + month + '-' + date + ' ' + hour + ':' + minute
}
const getNum = num => num > 9 ? num : '0' + num;

const hasMd5 = (value) => crypto.createHash("md5").update(value).digest("hex");

module.exports = {formatDateTime, hasMd5}